import React from 'react';
import Edu1Front from './Edu1Front';
import Edu1Back from './Edu1Back';

class Edu1 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showFront: true,
        }
        this.handleFront = this.handleFront.bind(this);
        this.handleBack = this.handleBack.bind(this);
    };

    // toggleBack = () => {
    //     if (this.state.showFront === true) {
    //         return <Edu1Front />
    //     } else {
    //         return <Edu1Back />
    //     }
    // }

    handleFrontClick = () => {
        this.setState({showFront: false})
    }

    handleBack = () => {
        this.setState({showFront: true})
    }

    render() {
        const showFront = this.state.showFront;
        let whattoshow;

        if(showFront) {
            whattoshow = <Edu1Front onClick={this.handleFrontClick} />
        }

        return (
            <div onClick={this.handleClick}>
                {this.toggleBack()}
            </div>
        )
    }
}

export default Edu1;
