import React from 'react';
import Edu1 from './Edu1';
import Edu2 from './Edu2';
import Edu3 from './Edu3';
import FlipCard from 'react-flipcard-2';

class Education extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isFlipped: false,
        }
    }

    showBack = () => {
        this.setState({isFlipped: true});
    }

    showFront = () => {
        this.setState({isFlipped: false});
    }

    handleOnFlip = (flipped) => {
        if (flipped) {
            this.refs.backButton.getDOMNode().focus();
        }
    }

    render() {
        return (
            <div>
                <FlipCard>
                    <div>
                        <div>Front</div>
                        <div><small>(horizontal flip)</small></div>
                    </div>
                    <div>Back</div>
                </FlipCard>
                {/* <FlipCard
                    disabled={true}
                    flipped={this.state.isFlipped}
                >
                    <div>
                        <div>Front</div>
                        <button type="button" onClick={this.showBack}>Show back</button>
                        <div><small>(manual flip)</small></div>
                    </div>
                    <div>
                        <div>Back</div>
                        <button type="button" ref="backButton" onClick={this.showFront}>Show front</button>
                    </div>
                </FlipCard> */}
            </div>
        );
    }
};

