import React from 'react';
import Edu1Front from './Edu1Front';
import Edu1Back from './Edu1Back';

class Edu1 extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showFront: true
        }
    };

    toggleBack = () => {
        if (this.state.showFront === true) {
            return <Edu1Front />
        } else {
            return <Edu1Back />
        }
    }
    handleClick = () => {
        this.setState({showFront: false})
    }

    render() {
        return (
            <div>
                {toggleBack()}
            </div>
        )
    }
}

export default Edu1;
