import React from 'react';
import Edu1 from './Edu1';
import Edu2 from './Edu2';
import Edu3 from './Edu3';
import FlipCard from 'react-flipcard-2';

class Education extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isFlipped: false,
        }
    }

    showBack = () => {
        this.setState({isFlipped: true});
    }

    showFront = () => {
        this.setState({isFlipped: false});
    }

    handleOnFlip = (flipped) => {
        if (flipped) {
            this.refs.backButton.getDOMNode().focus();
        }
    }

    handleKeyDown = (e) => {
        if (this.state.isFlipped && e.keyCode === 27) {
            this.showFront();
        }
    }

    render() {
        return (
            <Wrapper style={{height: '100%',  display: 'flex', alignItems: 'center', justifyContent: 'space-around'}}>
                <div>
                    <FlipCard>
                        <div>
                            <Edu1 />
                        </div>
                        <div>
                            <p>info</p>
                        </div>
                    </FlipCard>
                </div>
                <div>
                    <FlipCard>
                        <div>
                            <Edu2 />
                        </div>
                        <div>
                            <p>info</p>
                        </div>
                    </FlipCard>
                </div>
                <div>
                    <FlipCard>
                        <div>
                            <Edu3 />
                        </div>
                        <div>
                            <p>info</p>
                        </div>
                    </FlipCard>
                </div>
            </Wrapper>
        );
    }
};

