import React from 'react';
import Edu1Front from './Edu1Front';
import Edu1Back from './Edu1Back';
import Edu2 from './Edu2';
import Edu2Back from './Edu2Back';
import Edu3 from './Edu3';
import Edu3Back from './Edu3Back';

import './Education.css';

class Education extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            showBack: false,
        }
    };

    toggleBack = () => {
        if (this.state.showBack === false) {
            return <Edu1Front />
        } else {
            return <Edu1Back />
        }
    }

    render() {
        return (
            <div 
                className='education-page'
                style={{
                    display: 'flex',
                    flexDirection: 'row',
                    flexWrap: 'wrap',
                    alignContent: 'center',
                    justifyContent: 'center',
                }}
            >
                    <Edu1Front />
                    <Edu1Back />
                    <Edu2 />
                    <Edu2Back />
                    <Edu3 />
                    <Edu3Back />
            </div>
        )
    }
}

export default Education;
