import React from 'react';
import Particles from 'react-particles-js';
import Nav from './components/Nav.js';
import MyName from './components/MyName.js';
import AboutMe from './components/AboutMe.js';
import Education from './components/Education';
import Work from './components/Work';

import './App.css';

const particlesOptions = {
    particles: {
        number: {
            value: 30,
            density: {
                enable: true,
                value_area: 125
            }
        }
    }
}

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            route: 'home',
        }
    }

    triggerAboutMe = () => {
        this.setState({route: 'aboutMe'})
    }

    triggerEducation = () => {
        this.setState({route: 'edu'})
    }

    triggerWork = () => {
        this.setState({route: 'work'})
    }

    //THIS FUNCTION DOESN'T WORK YET
    renderComponent = () => {
        switch(this.state) {
            case 'home':
                return <MyName />
            case 'aboutMe':
                return <AboutMe />;
            case 'edu':
                return <Education />;
            case 'work':
                return <Work />;
            default:
                return <MyName />;
        }
    }

    render () {
        return (
            <div className='app'>
                <Particles className='particles' params={particlesOptions} />
                <Nav 
                    triggerAboutMe={this.triggerAboutMe} 
                    triggerEducation={this.triggerEducation} 
                    triggerWork={this.triggerWork} 
                />
                <div>
                    {this.renderComponent()}
                </div>
            </div>
        )
    }
}

export default App;

