import React from 'react';
import WorkExp1 from './WorkExp1';
import WorkExp1Back from './WorkExp1Back';
import WorkExp2 from './WorkExp2';
import WorkExp2Back from './WorkExp2Back';
import WorkExp3 from './WorkExp3';
import WorkExp3Back from './WorkExp3Back';
import WorkExp4 from './WorkExp4';
import WorkExp4Back from './WorkExp4Back';
import WorkExp5 from './WorkExp5';
import WorkExp5Back from './WorkExp5Back';
import WorkExp6 from './WorkExp6';
import WorkExp6Back from './WorkExp6Back';

import FlipCard from 'react-flipcard-2';

import './Work.css';

class Work extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isFlipped: false,
        }
    }

    showBack = () => {
        this.setState({isFlipped: true});
    }

    showFront = () => {
        this.setState({isFlipped: false});
    }

    handleOnFlip = (flipped) => {
        if (flipped) {
            this.refs.backButton.getDOMNode().focus();
        }
    }

    handleKeyDown = (e) => {
        if (this.state.isFlipped && e.keyCode === 27) {
            this.showFront();
        }
    }

    render() {
        return (
            <div 
                className='work-page'
                style={{
                    display: 'flex',
                    flexDirection: 'row',
                    alignContent: 'center',
                    justifyContent: 'space-around',
                }}
            >
                <div>
                    <FlipCard>
                        <div>
                            <WorkExp1 />
                        </div>
                        <div>
                            <WorkExp1Back />
                        </div>
                    </FlipCard>
                </div>
                <div>
                    <FlipCard>
                        <div>
                            <WorkExp2 />
                        </div>
                        <div>
                            <WorkExp2Back />
                        </div>
                    </FlipCard>
                </div>
                <div>
                    <FlipCard>
                        <div>
                            <WorkExp3 />
                        </div>
                        <div>
                            <WorkExp3Back />
                        </div>
                    </FlipCard>
                </div>
                <div>
                    <FlipCard>
                        <div>
                            <WorkExp4 />
                        </div>
                        <div>
                            <WorkExp4Back />
                        </div>
                    </FlipCard>
                </div>
                <div>
                    <FlipCard>
                        <div>
                            <WorkExp5 />
                        </div>
                        <div>
                            <WorkExp5Back />
                        </div>
                    </FlipCard>
                </div>
                <div>
                    <FlipCard>
                        <div>
                            <WorkExp6 />
                        </div>
                        <div>
                            <WorkExp6Back />
                        </div>
                    </FlipCard>
                </div>
            </div>
        );
    }
};

