import React from 'react';
import Edu1 from './Edu1';
import Edu1Back from './Edu1Back';
import Edu2 from './Edu2';
import Edu2Back from './Edu2Back';
import Edu3 from './Edu3';
import Edu3Back from './Edu3Back';

import FlipCard from 'react-flipcard-2';

import './Education.css';

class Education extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            isFlipped: false,
        }
    }

    showBack = () => {
        this.setState({isFlipped: true});
    }

    showFront = () => {
        this.setState({isFlipped: false});
    }

    handleOnFlip = (flipped) => {
        if (flipped) {
            this.refs.backButton.getDOMNode().focus();
        }
    }

    handleKeyDown = (e) => {
        if (this.state.isFlipped && e.keyCode === 27) {
            this.showFront();
        }
    }

    render() {
        return (
            <div className='education-page'>
                <div>
                    <FlipCard>
                        <div>
                            <Edu1 />
                        </div>
                        <div>
                            <Edu1Back />
                        </div>
                    </FlipCard>
                </div>
                <div>
                    <FlipCard>
                        <div>
                            <Edu2 />
                        </div>
                        <div>
                            <Edu2Back />
                        </div>
                    </FlipCard>
                </div>
                <div>
                    <FlipCard>
                        <div>
                            <Edu3 />
                        </div>
                        <div>
                            <Edu3Back />
                        </div>
                    </FlipCard>
                </div>
            </div>
        );
    }
};

// const Education = () => {
