import React from 'react';
import Particles from 'react-particles-js';
import Nav from './components/Nav.js';
import MyName from './components/MyName.js';
import AboutMe from './components/AboutMe.js';
import Education from './components/EduTab/Education';
import Work from './components/WorkTab/Work';
import ComponentToRender from './components/ComponentToRender';
import FlipCard from 'react-flipcard-2';

import './App.css';

const particlesOptions = {
    particles: {
        number: {
            value: 30,
            density: {
                enable: true,
                value_area: 125
            }
        }
    }
}

class App extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            route: '',
            isFlipped: false,
        }
    }

    triggerAboutMe = () => {
        this.setState({route: 'aboutMe'})
    }

    triggerEducation = () => {
        this.setState({route: 'edu'})
    }

    triggerWork = () => {
        this.setState({route: 'work'})
    }

    renderComponent = () => {
        if (this.state.route === 'aboutMe') {
            return <AboutMe />
        } else if (this.state.route === 'edu') {
            return <Education />
        } else if (this.state.route === 'work') {
            return <Work />
        } else {
            return <MyName />
        }
    }

    showBack() {
        this.setState({
        isFlipped: true
        });
    }

    showFront() {
        this.setState({
        isFlipped: false
        });
    }

    handleOnFlip(flipped) {
        if (flipped) {
        this.refs.backButton.getDOMNode().focus();
        }
    }

    handleKeyDown(e) {
        if (this.state.isFlipped && e.keyCode === 27) {
        this.showFront();
        }
    }

    render () {
        return (
            <div className='app'>
                <Particles className='particles' params={particlesOptions} />
                <Nav 
                    triggerAboutMe={this.triggerAboutMe} 
                    triggerEducation={this.triggerEducation} 
                    triggerWork={this.triggerWork} 
                />
                <ComponentToRender renderComponent={this.renderComponent} />
                <FlipCard
                    disabled={true}
                    flipped={this.state.isFlipped}
                    onFlip={this.handleOnFlip}
                    onKeyDown={this.handleKeyDown}
                >
                    <div>
                        <div>Front</div>
                        <button type="button" onClick={this.showBack}>Show back</button>
                        <div><small>(manual flip)</small></div>
                    </div>
                    <div>
                        <div>Back</div>
                        <button type="button" ref="backButton" onClick={this.showFront}>Show front</button>
                    </div>
                </FlipCard>
            </div>
        )
    }
}

export default App;
